import React,{useContext} from 'react'
import {CartContext} from '../context/Global'
export default function Cart() {
    const [cart,setcart] = useContext(CartContext)
    const totalamount = cart.reduce((acc,curr) => acc + curr.price,0)
    return (
        <div>
          {cart.map((item) => (
             <div key={item.id}>
                 <h1>{item.text}</h1>
                 <p>{item.price}</p>
                 <p>{item.def}</p>
             </div>
          ))}
          {totalamount}


        </div>
    )
}
