import React, { useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import { CartContext } from '../context/Global'
import './shopitemslist.css'
import Countup from 'react-countup'
import FlipMove from 'react-flip-move'
import Button from '@material-ui/core/Button';
import Icon from '@material-ui/core/Icon';
import Typography from '@material-ui/core/Typography';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogTitle from '@material-ui/core/DialogTitle';
import useMediaQuery from '@material-ui/core/useMediaQuery';
import { useTheme } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        // margin: '5px 0px',
        flexWrap: 'wrap',
        '& > *': {
            margin: theme.spacing(1),
            width: theme.spacing(16),
            height: theme.spacing(16),
        },
    },
    width: {
        width: '350px',
        height: '400px',
        margin: 'auto',
    }
}));

export default function Cart() {
    const [open, setOpen] = React.useState(false);
    const theme = useTheme();
    const fullScreen = useMediaQuery(theme.breakpoints.down('sm'));


    const classes = useStyles();
    const [cart, setcart] = useContext(CartContext)
    const totalamount = cart.reduce((acc, curr) => acc + curr.price, 0)

    const handlers = (id) => {
        var newcart = [...cart]
        newcart.splice(id, 1)
        setcart(newcart)
    }
    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = (id) => {
        var newcart = [...cart]
        newcart.splice(id, 1)
        setcart(newcart)
        setOpen(false);
    };
    const handledisagree = () => {
        setOpen(false);
    };
    return (
        <div>
            <div className="amount j">
                <div>
                    <h1 className="o">Items in Cart = {cart.length}</h1>
                </div>
                <div>
                    <h1 className="o">Total Amount = <Countup start={0} end={totalamount} duration={2.75} /></h1>
                </div>
            </div>

            <div className={classes.root}>

                {cart.map((item, id) => (

                    <Paper elevation={3} className={classes.width} key={id}>
                        <Dialog
                            fullScreen={fullScreen}
                            open={open}
                            onClose={handleClose}
                            aria-labelledby="responsive-dialog-title"
                        >
                            <h1>{item.text}</h1>
                            <img src={item.image} alt="" width="200px" height='200px' />
                            <Typography>
                                this is new shirt in market
                        </Typography>
                            <Button
                                onClick={handlers}
                                variant="contained"
                                color="primary"
                                className={classes.button}
                                endIcon={<Icon>delete</Icon>}
                            >
                                Delete
                   </Button>




                            <DialogTitle id="responsive-dialog-title">{"Are you sure you want to buy this cool shirt"}</DialogTitle>
                            <img src={item.image} alt="" />
                            <DialogActions>
                         <Button autoFocus onClick={handledisagree}  color="primary">
                             Disagree
                    </Button>
                         <Button onClick={handledisagree}  color="primary" autoFocus>
                             Agree
                    </Button>
                     </DialogActions>
                            </Dialog>
                            <Button varient="contained" onClick={handleClickOpen} color="secondary" className={classes.button}>CheckOut</Button>
                    </Paper>
                ))}
            </div>
        </div>
    );
}
