import React, { useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import { CartContext } from '../context/Global'
import './shopitemslist.css'
import Countup from 'react-countup'
import FlipMove from 'react-flip-move'
import Button from '@material-ui/core/Button';
import Icon from '@material-ui/core/Icon';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        // margin: '5px 0px',
        flexWrap: 'wrap',
        '& > *': {
            margin: theme.spacing(1),
            width: theme.spacing(16),
            height: theme.spacing(16),
        },
    },
    width: {
        width: '350px',
        height: '400px'
    }
}));

export default function SimplePaper() {
    const classes = useStyles();
    const [cart, setcart] = useContext(CartContext)
    const totalamount = cart.reduce((acc, curr) => acc + curr.price, 0)
    const handlers = (id) => {
        var newcart = [...cart]
        newcart.splice(id, 1)
        setcart(newcart)
    }
    console.log(cart)
    return (
        <div>
            <div className="amount j">
                <div>
                    <h1 className="o">Items in Cart = {cart.length}</h1>
                </div>
                <div>
                    <h1 className="o">Total Amount = <Countup start={0} end={totalamount} duration={2.75} /></h1>
                </div>
            </div>

            <div className={classes.root}>
                {cart.map((item, id) => (
                    <Paper elevation={3} className={classes.width} key={id}>
                        <h1>{item.text}</h1>
                        <img src={item.image} alt="" width="200px" height='200px'/>
                        <Typography>
                            this is new shirt in market
                        </Typography>
                        {/* <Button
                            onClick={handlers}
                            variant="contained"
                            color="primary"
                            className={classes.button}
                            endIcon={<Icon>delete</Icon>}
                        >
                            Delete
                   </Button> */}
                    </Paper>
                ))}
            </div>
        </div>
    );
}
